  //     }
  //   })
  //     .then(res => {
  //       if (!res.ok) {
  //         return res.json().then(error => Promise.reject(error));
  //       }
  //       return res.json();
  //     })
  //     .then(todos => {
  //       this.setState({ todos: todos });
  //     })
  //     .catch(error => {
  //       this.setState({ error });
  //     });
  // }

  render() {
    return (
      <TodoContext.Provider value={this.state}>
        <Fragment>
          <div className="container">
            <InputTodo />
            <EditTodo />
            <ListTodo />
          </div>
        </Fragment>
      </TodoContext.Provider>
    );
  }
}

export default App;
