import React, { Component, Fragment } from 'react';
import EditTodo from '../EditTodo/EditTodo';
import TodoContext from '../Context/TodoContext';

class ListTodos extends Component {
  static contextType = TodoContext;

  deleteTodo = (e, todoId) => {
    e.preventDefault();
    this.context.deleteTodo(todoId);
  };

  updateTodo = e => {
    e.preventDefault();
    const newImage = { image: e.target.bandImage.value };
    if (newImage.image.includes('jpg' || 'png')) {
      this.context.updateUser(newImage);
      e.target.reset();
    } else {
      alert('Only valid image links (jpg, png) are allowed');
    }
  };

  render() {
    return (
      <Fragment>
        {' '}
        <table className="table mt-5 text-center">
          <thead>
            <tr>
              <th>Description</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {this.context.todos.map(todo => {
              return (
                <tr key={todo.id}>
                  <td>{todo.description}</td>
                  <td>
                    <EditTodo todo={todo} />
                  </td>
                  <td>
                    <button
                      className="btn btn-danger"
                      type="submit"
                      name="delete"
                      onClick={e => this.deleteTodo(e, todo.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
            {/* <tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          </tbody>
        </table>
      </Fragment>
    );
  }
}

export default ListTodos;
